import './App.css';
import React, { createElement } from 'react';
import { render } from 'react-dom';

function Button(props) {
  var buttonName = props.buttonName;

  return (
    <button className={props.buttonClass} onClick={() => {
        const min = 0;
        const max = 3;
        const rand = min + Math.random() * (max - min);
        var botChoice;

        if (rand <= 1)
          botChoice = "The bot choose Rock !";
        else if (rand <= 2)
          botChoice = "The bot choose Paper !";
        else
          botChoice = "The bot choose Scissors !";
        document.getElementById("choiceJ1").textContent = "You choose " + buttonName + " !";
        document.getElementById("choiceJ2").textContent = botChoice;
        document.getElementById("ptn").textContent = document.getElementById("ptn").textContent[0] + 1 + "/0";
      }}>{buttonName}</button>
  );
}

class App extends React.Component {
  constructor(props) {
    super(props); 
  }


  render() {
    return (
      <div className="container">
				<div className="point">
        	<p id="ptn">0/0</p>
				</div>
				<div className="choice">
        	<p>Choose your weapon !</p>
				</div>
				<div className="j1">
        	<p id="choiceJ1"></p>
				</div>
				<div className="j2">
        	<p id="choiceJ2"></p>
				</div>
        <Button buttonName={"Rock"} buttonClass={"Rock"}/>
        <Button buttonName={"Paper"} buttonClass={"Paper"}/>
        <Button buttonName={"Scissors"} buttonClass={"Scissors"}/>
        <Button buttonName={"Restart"} buttonClass={"Restart"}/>
      </div>
    );
  }
}

export default App;
