import logo from './logo.svg';
import './App.css';
import React, { createElement } from 'react';

function Button(props) {
  return (
    <button className={props.buttonClass}>{props.buttonName}</button>
  );
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.buttonChoosen = "";
  }

  handleClick(buttonChoosen) {
    var parent = document.getElementsByClassName('App');
    var child = document.getElementsByClassName('text');
    parent.removeChild(child);
    this.buttonChoosen = {buttonChoosen};
  }

  render() {
    return (
      <div className="App">
        <p class="text">{this.buttonChoosen}</p>
        <Button buttonName={"Rock"} buttonClass={"Rock"} onClick={this.handleClick("Rock")}/>
        <Button buttonName={"Paper"} buttonClass={"Paper"} onClick={this.handleClick("Paper")}/>
        <Button buttonName={"Scissors"} buttonClass={"Scissors"} onClick={this.handleClick("Scissors")}/>
      </div>
    );
  }
}

export default App;
