import './App.css';
import React, { createElement } from 'react';

class App extends React.Component {
  constructor(props) {
    super(props); 
		this.pointJ1 = 0;
		this.pointJ2 = 0;
		this.botChoice = "Rock";
  }

	Button(props) {
		var buttonName = props.buttonName;
		var point = "0/0";

		return (
			<button className={props.buttonClass} onClick={() => {
					document.getElementById("choiceJ1").textContent = "You choose " + buttonName + " !";
					document.getElementById("choiceJ2").textContent = "The bot choose Rock !";
					document.getElementById("ptn").textContent = point;
				}}>{buttonName}</button>
		);
	}

  render() {
    return (
      <div className="container">
				<div className="point">
        	<p id="ptn">0/0</p>
				</div>
				<div className="choice">
        	<p>Choose your weapon !</p>
				</div>
				<div className="j1">
        	<p id="choiceJ1"></p>
				</div>
				<div className="j2">
        	<p id="choiceJ2"></p>
				</div>
        <this.Button buttonName={"Rock"} buttonClass={"Rock"}/>
        <this.Button buttonName={"Paper"} buttonClass={"Paper"}/>
        <this.Button buttonName={"Scissors"} buttonClass={"Scissors"}/>
      </div>
    );
  }
}

export default App;
