import './App.css';
import React, { createElement } from 'react';
import { render } from 'react-dom';

function Button(props) {
  var buttonName = props.buttonName;

  return (
    <button className={props.buttonClass} onClick={() => {
        const min = 0;
        const max = 3;
        const rand = min + Math.random() * (max - min);
        var botChoice;
        var pointJ1 = document.getElementById("ptn").textContent[0];
        var pointJ2 = document.getElementById("ptn").textContent[2];
        var J1Content = document.getElementById("choiceJ1").textContent;
        var J2Content = document.getElementById("choiceJ2").textContent;

        pointJ1 = parseInt(pointJ1);
        pointJ2 = parseInt(pointJ2);
        if (rand <= 1)
          botChoice = "The bot choose Rock !";
        else if (rand <= 2)
          botChoice = "The bot choose Paper !";
        else
          botChoice = "The bot choose Scissors !";
        J1Content = "You choose " + buttonName + " !";
        J2Content = botChoice;
        document.getElementById("ptn").textContent = pointJ1 + "/" + pointJ2;
      }}>{buttonName}</button>
  );
}

class App extends React.Component {
  constructor(props) {
    super(props); 
  }


  render() {
    return (
      <div className="container">
				<div className="point">
        	<p id="ptn">0/0</p>
				</div>
				<div className="choice">
        	<p>Choose your weapon ! The first one to 3 points win !</p>
				</div>
				<div className="j1">
        	<p id="choiceJ1"></p>
				</div>
				<div className="j2">
        	<p id="choiceJ2"></p>
				</div>
        <Button buttonName={"Rock"} buttonClass={"Rock"}/>
        <Button buttonName={"Paper"} buttonClass={"Paper"}/>
        <Button buttonName={"Scissors"} buttonClass={"Scissors"}/>
        <button className="restart" onClick={() => {
          document.getElementById("choiceJ1").textContent = "";
          document.getElementById("choiceJ2").textContent = "";
          document.getElementById("ptn").textContent = "0/0";
      }}>Restars</button>
      </div>
    );
  }
}

export default App;
